      Tone.Transport.bpm.value = 121.80;
      Tone.Transport.start();
    }

    if(key == 'a'){
      if(drums.state == "started"){
        drums.stop();  
      } else{
        // start aligned with the next 'measure' subdivision
        drums.start("@1m");
      }
    }
    else if (key == 's'){
      if(chords.state == "started"){
        chords.stop();  
      } else{
        chords.start("@1m");
      }
    }
    else if (key == 'd'){
      if(melody.state == "started"){
        melody.stop();  
      } else{
        melody.start("@1m");
      }
    }
    else if (key == 'f'){
      if(bass.state == "started"){
        bass.stop();  
      } else{
        bass.start("@1m");
      }
    }
  }
}

