  if (beat == 0) {
    kit.player("kick").volume.rampTo(2, 0.05);
  } else {
    kit.player("kick").volume.rampTo(-10, 0.05);
  }
  kit.player("kick").start(time);

  kickBrightness = 100;
  kickFlash = random(60, 0);  
}

