function scanGray(){

  // 移动长方体并检查每个块内的主要灰度等级
  if (currentBlockX >= width) {
    currentBlockX=0;

  }
  let i = floor(currentBlockX / widthX);//第i格
  // let j = floor(random(blockSize));//遍历y

