// Second step: with sync + exclusive loop per track
let trackNames = ["Drums", "Bass", "Chords", "Melody"];
let displayNames = ["MyName", "Bass", "Chords", "Melody"];
let loops = [];
let n = 2; //number of alternative loops per track

let startBtn;

function setup() {  
  noCanvas();  
  loadLoops();
}

