class Loop {
  constructor(url, i, j, name) {
    this.player = new Tone.Player({
      url: url,
      loop: true
    });
    this.player.toDestination();
    this.quantize = "@2m"
    this.siblings = []; // loops from the same track

    this.button = createButton(name);
    this.button.mousePressed(this.toggle.bind(this));
    this.button.style('width', '100px');
    this.button.style('height', '60px');
    this.button.position(j * 100, i * 60);
    this.button.style('background-color', 'white');
  }

  toggle() {
    if(Tone.Transport.state != "started"){
      Tone.start();
      Tone.Transport.start();
    }
    // if the loop is playing, mark it to stop at the next measure
    if (this.player.state == "started") {
      this.scheduleStop();
    }
    // if the loop is not playing, mark it to start at the next measure
    else {
      this.scheduleStart();
    }
  }

  start() {
    this.button.style('background-color', 'black');
    this.button.style('color', 'white');
    this.state = this.player.state;
  }

  transition(){
    this.button.style('background-color', 'orange');
    this.button.style('color', 'black');
  }

  stop() {
    this.button.style('background-color', 'white');
    this.button.style('color', 'black');
    this.state = this.player.state;
  }

  scheduleStart() {
    this.player.start(this.quantize);

    // stop any siblings that are currently playing: 
    for (const sibling of this.siblings) {
      if (sibling.player.state == 'started') {
        sibling.scheduleStop();
      }
    }

    // visuals
    //immediately: call transition function (sets orange state)
    this.transition();

    //at the next this.quantize line: call start function (sets the black state)
    Tone.Draw.schedule(() => { this.start() }, this.quantize);
  }

  scheduleStop() {
    this.player.stop(this.quantize);
    Tone.Draw.schedule(this.stop.bind(this), this.quantize);

    this.button.style('background-color', 'orange');
    this.button.style('color', 'black');
  }

}

// Second step: with sync + exclusive loop per track
let trackNames = ["Drums", "Bass", "Chords", "Melody"];
let displayNames = ["https://i.natgeofe.com/n/548467d8-c5f1-4551-9f58-6817a8d2c45e/NationalGeographic_2572187_3x4.jpg", "Bass", "Chords", "Melody", ""];
let loops = [];
let n = 2; //number of alternative loops per track

let startBtn;

function setup() {  
  noCanvas();  
  loadLoops();
}

function draw() {
  // not drawing anything for now
}

function loadLoops() {  
  Tone.Transport.bpm.value = 122;
  for(let i = 0; i < trackNames.length; i++){    
    loops[i] = [];
    // load n loop alternatives per track
    for(let j = 0; j < n; j++){
      let name = trackNames[i] + " " + j;
      loops[i][j] = new Loop("loops/" + trackNames[i] + j + ".mp3", i, j, name, displayNames[i]);
    }
    // load loop siblings (each needs to access the loops from the same track, 
    // to stop them when it's about to play - 
    // we want only one loop from each track to be playing at any given time.
    for(let j = 0; j < n; j++){
      for(let k = 0; k < n; k++){
        if(j != k){
          loops[i][j].siblings.push(loops[i][k])
        }
      }
    }
  }
}

